import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { FiPlus, FiExternalLink, FiTrash2, FiEdit2, FiX, FiSave, FiImage } from 'react-icons/fi';
import { authService } from '../services/authService';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: none; }
`;

const Container = styled.div`
  padding: 20px 16px;
  max-width: 900px;
  margin: 0 auto;
  @media (max-width: 768px) { padding: 16px 12px; }
`;

const Title = styled.h1`
  margin: 0 0 20px 0;
  font-size: 28px;
  font-weight: 700;
  background: linear-gradient(135deg, var(--neon-1), var(--neon-3));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 12px;
    align-items: stretch;
  }
`;

const AddButton = styled.button`
  padding: 12px 24px;
  border: none;
  border-radius: 12px;
  background: linear-gradient(135deg, var(--neon-1), var(--neon-3));
  color: #fff;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  box-shadow: 0 4px 16px rgba(124, 58, 237, 0.3);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(124, 58, 237, 0.4);
  }
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.06);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 18px;
  overflow: hidden;
  box-shadow: 0 6px 20px rgba(0,0,0,0.15);
  margin-bottom: 16px;
  animation: ${fadeIn} 0.3s ease-out;

  @supports (backdrop-filter: blur(10px)) {
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }
`;

const WishlistImage = styled.div`
  height: 200px;
  background: ${({ image }) =>
    image
      ? `url(${image})`
      : 'linear-gradient(135deg, rgba(124,58,237,0.3), rgba(255,107,138,0.3))'};
  background-size: cover;
  background-position: center;
  position: relative;
  cursor: pointer;

  &:hover {
    &::after {
      content: "Cliquer pour agrandir";
      position: absolute;
      bottom: 10px;
      left: 50%;
      transform: translateX(-50%);
      background: rgba(0,0,0,0.7);
      color: #fff;
      padding: 6px 12px;
      border-radius: 8px;
      font-size: 12px;
    }
  }
`;

const StatusBadge = styled.div`
  position: absolute;
  top: 12px;
  right: 12px;
  padding: 6px 14px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  color: #fff;
  background: ${p =>
    p.status === 'gifted' ? 'rgba(16, 185, 129, 0.9)' :
    p.status === 'bought' ? 'rgba(243, 156, 18, 0.9)' :
    'rgba(52, 152, 219, 0.9)'
  };
`;

const WishlistContent = styled.div`
  padding: 20px;
`;

const WishlistTitle = styled.h3`
  margin: 0 0 8px 0;
  font-size: 20px;
  color: var(--text-color);
`;

const ForUser = styled.p`
  color: var(--neon-3);
  font-size: 14px;
  margin: 0 0 12px 0;
  font-weight: 500;
`;

const Description = styled.p`
  color: var(--muted-text);
  font-size: 14px;
  line-height: 1.6;
  margin: 0 0 16px 0;
`;

const Actions = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
`;

const ActionButton = styled.button`
  padding: 8px 16px;
  border: 2px solid rgba(255, 255, 255, 0.12);
  background: rgba(255, 255, 255, 0.04);
  color: var(--text-color);
  border-radius: 10px;
  cursor: pointer;
  font-size: 13px;
  transition: all 0.2s ease;
  display: inline-flex;
  align-items: center;
  gap: 6px;

  &:hover {
    border-color: var(--neon-1);
    background: rgba(255, 255, 255, 0.08);
  }

  &.active {
    border-color: var(--neon-1);
    background: var(--neon-1);
    color: #fff;
  }
`;

const DeleteButton = styled(ActionButton)`
  border-color: rgba(231, 76, 60, 0.5);
  color: #e74c3c;

  &:hover {
    border-color: #e74c3c;
    background: rgba(231, 76, 60, 0.1);
  }
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0,0,0,0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;

  @media (max-width: 768px) {
    align-items: flex-start;
    padding-top: 20px;
  }
`;

const ModalContent = styled.div`
  background: rgba(15, 18, 33, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 18px;
  padding: 28px;
  width: 100%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;

  @supports (backdrop-filter: blur(14px)) {
    backdrop-filter: blur(14px);
    -webkit-backdrop-filter: blur(14px);
  }
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const ModalTitle = styled.h2`
  margin: 0;
  font-size: 22px;
  color: var(--text-color);
`;

const CloseButton = styled.button`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: none;
  background: rgba(255, 255, 255, 0.1);
  color: var(--text-color);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: rgba(255, 255, 255, 0.15);
  }
`;

const FormGrid = styled.div`
  display: grid;
  gap: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 14px 16px;
  border: 2px solid rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  font-size: 15px;
  color: var(--text-color);
  background: rgba(255, 255, 255, 0.04);
  box-sizing: border-box;

  &:focus {
    outline: none;
    border-color: var(--neon-1);
    box-shadow: 0 0 0 4px rgba(124, 58, 237, 0.12);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 14px 16px;
  border: 2px solid rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  font-size: 15px;
  color: var(--text-color);
  background: rgba(255, 255, 255, 0.04);
  box-sizing: border-box;
  min-height: 100px;
  resize: vertical;
  font-family: inherit;

  &:focus {
    outline: none;
    border-color: var(--neon-1);
    box-shadow: 0 0 0 4px rgba(124, 58, 237, 0.12);
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 14px 16px;
  border: 2px solid rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  font-size: 15px;
  color: var(--text-color);
  background: rgba(255, 255, 255, 0.04);
  box-sizing: border-box;
  cursor: pointer;

  &:focus {
    outline: none;
    border-color: var(--neon-1);
    box-shadow: 0 0 0 4px rgba(124, 58, 237, 0.12);
  }
`;

const SaveButton = styled.button`
  width: 100%;
  padding: 14px;
  border: none;
  border-radius: 12px;
  background: linear-gradient(135deg, var(--neon-1), var(--neon-3));
  color: #fff;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  box-shadow: 0 4px 16px rgba(124, 58, 237, 0.3);

  &:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(124, 58, 237, 0.4);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  color: var(--muted-text);
`;

export default function Wishlist() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editing, setEditing] = useState(null);
  const [form, setForm] = useState({
    title: '',
    description: '',
    link_url: '',
    image_url: '',
    recipient_id: '',
    status: 'idea'
  });
  const [couples, setCouples] = useState([]);

  useEffect(() => {
    load();
    loadCouples();
  }, []);

  const load = async () => {
    try {
      const r = await authService.api.get('/api/wishlist');
      setItems(r.data);
    } catch (e) {
      console.error(e);
    }
    setLoading(false);
  };

  const loadCouples = async () => {
    try {
      const r = await authService.api.get('/api/couples');
      setCouples(r.data);
    } catch (e) {
      console.error(e);
    }
  };

  const openModal = (item = null) => {
    if (item) {
      setEditing(item._id);
      setForm({
        title: item.title || '',
        description: item.description || '',
        link_url: item.link_url || '',
        image_url: item.image_url || '',
        recipient_id: item.recipient_id || '',
        status: item.status || 'idea'
      });
    } else {
      setEditing(null);
      setForm({ title: '', description: '', link_url: '', image_url: '', recipient_id: '', status: 'idea' });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditing(null);
  };

  const save = async () => {
    if (!form.title.trim()) return;
    try {
      if (editing) {
        await authService.api.put(`/api/wishlist/${editing}`, form);
      } else {
        await authService.api.post('/api/wishlist', form);
      }
      closeModal();
      await load();
    } catch (e) {
      alert('Erreur: ' + (e?.response?.data?.error || e.message));
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await authService.api.put(`/api/wishlist/${id}`, { status });
      await load();
    } catch (e) {
      console.error(e);
    }
  };

  const remove = async (id) => {
    if (!window.confirm('Supprimer cet √©l√©ment ?')) return;
    try {
      await authService.api.delete(`/api/wishlist/${id}`);
      await load();
    } catch (e) {
      console.error(e);
    }
  };

  const getStatusLabel = (status) => {
    switch (status) {
      case 'idea': return 'Id√©e';
      case 'bought': return 'Achet√©';
      case 'gifted': return 'Offert';
      default: return status;
    }
  };

  const getUserName = (item) => {
    if (!item.recipient_id) return 'Moi';
    const couple = couples.find(c => c._id === item.recipient_id);
    return couple ? couple.name : 'Inconnu';
  };

  const getImageUrl = (item) => {
    if (item.image_url) return item.image_url;
    if (item.images && item.images[0]) {
      const img = item.images[0];
      if (typeof img === 'string') return img;
      if (img.url) return img.url;
    }
    return null;
  };

  return (
    <Container>
      <Header>
        <Title>Wishlist</Title>
        <AddButton onClick={() => openModal()}>
          <FiPlus /> Ajouter
        </AddButton>
      </Header>

      {loading ? (
        <>
          <div className="skeleton" style={{ height: 320, borderRadius: 18, marginBottom: 16 }} />
          <div className="skeleton" style={{ height: 320, borderRadius: 18 }} />
        </>
      ) : items.length === 0 ? (
        <EmptyState>
          <div style={{ fontSize: 48, marginBottom: 12 }}>üéÅ</div>
          <div style={{ fontSize: 18, fontWeight: 600, marginBottom: 8 }}>Votre wishlist est vide</div>
          <div>Ajoutez vos id√©es cadeaux pour vous ou votre partenaire</div>
        </EmptyState>
      ) : (
        items.map(item => (
          <Card key={item._id}>
            <WishlistImage image={getImageUrl(item)}>
              <StatusBadge status={item.status}>
                {getStatusLabel(item.status)}
              </StatusBadge>
            </WishlistImage>
            <WishlistContent>
              <WishlistTitle>{item.title}</WishlistTitle>
              <ForUser>Pour {getUserName(item)}</ForUser>
              {item.description && <Description>{item.description}</Description>}
              <Actions>
                <ActionButton
                  className={item.status === 'idea' ? 'active' : ''}
                  onClick={() => updateStatus(item._id, 'idea')}
                >
                  Id√©e
                </ActionButton>
                <ActionButton
                  className={item.status === 'bought' ? 'active' : ''}
                  onClick={() => updateStatus(item._id, 'bought')}
                >
                  Achet√©
                </ActionButton>
                <ActionButton
                  className={item.status === 'gifted' ? 'active' : ''}
                  onClick={() => updateStatus(item._id, 'gifted')}
                >
                  Offert
                </ActionButton>
                {item.link_url && (
                  <ActionButton as="a" href={item.link_url} target="_blank" rel="noopener noreferrer">
                    <FiExternalLink /> Lien
                  </ActionButton>
                )}
                <ActionButton onClick={() => openModal(item)}>
                  <FiEdit2 /> Modifier
                </ActionButton>
                <DeleteButton onClick={() => remove(item._id)}>
                  <FiTrash2 /> Supprimer
                </DeleteButton>
              </Actions>
            </WishlistContent>
          </Card>
        ))
      )}

      {showModal && (
        <Modal onClick={(e) => e.target === e.currentTarget && closeModal()}>
          <ModalContent>
            <ModalHeader>
              <ModalTitle>{editing ? 'Modifier' : 'Ajouter'} un √©l√©ment</ModalTitle>
              <CloseButton onClick={closeModal}>
                <FiX />
              </CloseButton>
            </ModalHeader>
            <FormGrid>
              <Input
                placeholder="Titre *"
                value={form.title}
                onChange={e => setForm({ ...form, title: e.target.value })}
              />
              <TextArea
                placeholder="Description"
                value={form.description}
                onChange={e => setForm({ ...form, description: e.target.value })}
              />
              <Input
                placeholder="URL de l'image"
                value={form.image_url}
                onChange={e => setForm({ ...form, image_url: e.target.value })}
              />
              <Input
                placeholder="Lien (https://...)"
                value={form.link_url}
                onChange={e => setForm({ ...form, link_url: e.target.value })}
              />
              <Select value={form.recipient_id} onChange={e => setForm({ ...form, recipient_id: e.target.value })}>
                <option value="">Moi</option>
                {couples.map(c => (
                  <option key={c._id} value={c._id}>{c.name}</option>
                ))}
              </Select>
              <Select value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
                <option value="idea">Id√©e</option>
                <option value="bought">Achet√©</option>
                <option value="gifted">Offert</option>
              </Select>
              <SaveButton onClick={save} disabled={!form.title.trim()}>
                <FiSave /> {editing ? 'Sauvegarder' : 'Ajouter'}
              </SaveButton>
            </FormGrid>
          </ModalContent>
        </Modal>
      )}
    </Container>
  );
}
