import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { FiUpload, FiImage, FiX, FiSave } from 'react-icons/fi';
import { authService } from '../services/authService';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: none; }
`;

const Container = styled.div`
  padding: 20px 16px;
  max-width: 1000px;
  margin: 0 auto;
  @media (max-width: 768px) { padding: 16px 12px; }
`;

const Title = styled.h1`
  margin: 0 0 20px 0;
  font-size: 28px;
  font-weight: 700;
  background: linear-gradient(135deg, var(--neon-1), var(--neon-3));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.06);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 18px;
  padding: 24px;
  box-shadow: 0 6px 20px rgba(0,0,0,0.15);
  margin-bottom: 24px;
  animation: ${fadeIn} 0.3s ease-out;

  @supports (backdrop-filter: blur(10px)) {
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }
`;

const FileInputLabel = styled.label`
  display: block;
  padding: 40px 20px;
  border: 2px dashed rgba(255, 255, 255, 0.2);
  border-radius: 12px;
  background: rgba(255, 255, 255, 0.04);
  color: var(--text-color);
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: center;

  &:hover {
    border-color: var(--neon-1);
    background: rgba(255, 255, 255, 0.06);
  }
`;

const FileInput = styled.input`
  display: none;
`;

const PreviewGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 16px;
  margin-top: 20px;
`;

const PreviewCard = styled.div`
  background: rgba(255, 255, 255, 0.04);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 14px;
  overflow: hidden;
  position: relative;
`;

const PreviewImg = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const PreviewCaption = styled.textarea`
  width: 100%;
  padding: 10px;
  border: none;
  background: rgba(255, 255, 255, 0.06);
  color: var(--text-color);
  font-size: 13px;
  resize: none;
  box-sizing: border-box;
  font-family: inherit;

  &::placeholder {
    color: var(--muted-text);
  }

  &:focus {
    outline: none;
    background: rgba(255, 255, 255, 0.08);
  }
`;

const RemoveBtn = styled.button`
  position: absolute;
  top: 8px;
  right: 8px;
  width: 28px;
  height: 28px;
  border-radius: 50%;
  border: none;
  background: rgba(231, 76, 60, 0.9);
  color: #fff;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;

  &:hover {
    background: #e74c3c;
    transform: scale(1.1);
  }
`;

const Button = styled.button`
  padding: 14px 28px;
  border: none;
  border-radius: 12px;
  background: linear-gradient(135deg, var(--neon-1), var(--neon-3));
  color: #fff;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  box-shadow: 0 4px 16px rgba(124, 58, 237, 0.3);

  &:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(124, 58, 237, 0.4);
  }

  &:active {
    transform: translateY(0);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 14px;
  animation: ${fadeIn} 0.4s ease-out;

  @media (max-width: 768px) {
    grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
  }
`;

const PhotoCard = styled.div`
  aspect-ratio: 1;
  border-radius: 14px;
  overflow: hidden;
  position: relative;
  background: rgba(255, 255, 255, 0.04);
  border: 1px solid rgba(255, 255, 255, 0.08);
  transition: all 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 24px rgba(0,0,0,0.25);
  }
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const getPhotoUrl = (url) => {
  if (!url) return '';
  if (url.startsWith('http')) return url;
  const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';
  return `${API_BASE}${url}`;
};

const PhotoCaption = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 10px;
  background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
  color: #fff;
  font-size: 12px;
  opacity: 0;
  transition: opacity 0.3s ease;

  ${PhotoCard}:hover & {
    opacity: 1;
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 60px 20px;
  color: var(--muted-text);
  animation: ${fadeIn} 0.4s ease-out;
`;

export default function Photos() {
  const [items, setItems] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  useEffect(() => { load(); }, []);

  const load = async () => {
    try {
      const r = await authService.api.get('/api/photos');
      setItems(r.data);
    } catch (e) {
      console.error(e);
    }
    setLoading(false);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    const newPreviews = files.map(file => ({
      file,
      preview: URL.createObjectURL(file),
      caption: ''
    }));
    setPreviews(prev => [...prev, ...newPreviews]);
  };

  const updateCaption = (index, caption) => {
    setPreviews(prev => prev.map((p, i) => i === index ? { ...p, caption } : p));
  };

  const removePreview = (index) => {
    setPreviews(prev => prev.filter((_, i) => i !== index));
  };

  const upload = async () => {
    if (previews.length === 0) return;
    setUploading(true);
    try {
      for (const preview of previews) {
        await authService.photoService.uploadMultipart([preview.file]);
        // TODO: envoyer caption aussi si backend supporte
      }
      setPreviews([]);
      await load();
    } catch (e) {
      alert('Upload échoué: ' + e.message);
    }
    setUploading(false);
  };

  return (
    <Container>
      <Title>Photos</Title>

      {previews.length > 0 && (
        <Card>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 }}>
            <div style={{ fontSize: 16, fontWeight: 600 }}>
              {previews.length} photo{previews.length > 1 ? 's' : ''} à uploader
            </div>
            <Button onClick={upload} disabled={uploading}>
              <FiSave /> {uploading ? 'Upload...' : 'Sauvegarder'}
            </Button>
          </div>
          <PreviewGrid>
            {previews.map((p, i) => (
              <PreviewCard key={i}>
                <PreviewImg src={p.preview} alt="" />
                <RemoveBtn onClick={() => removePreview(i)}>
                  <FiX />
                </RemoveBtn>
                <PreviewCaption
                  placeholder="Ajouter une légende, note, contexte..."
                  value={p.caption}
                  onChange={(e) => updateCaption(i, e.target.value)}
                  rows={2}
                />
              </PreviewCard>
            ))}
          </PreviewGrid>
        </Card>
      )}

      <Card>
        <FileInputLabel htmlFor="photo-input">
          <div style={{ fontSize: 48, marginBottom: 12 }}><FiUpload /></div>
          <div style={{ fontSize: 16, fontWeight: 600, marginBottom: 4 }}>
            Cliquez pour sélectionner des photos
          </div>
          <div style={{ fontSize: 13, color: 'var(--muted-text)' }}>
            Vous pourrez ajouter des notes avant d'uploader
          </div>
        </FileInputLabel>
        <FileInput
          id="photo-input"
          type="file"
          accept="image/*"
          multiple
          onChange={handleFileSelect}
        />
      </Card>

      {loading ? (
        <Grid>
          <div className="skeleton" style={{ aspectRatio: 1, borderRadius: 14 }} />
          <div className="skeleton" style={{ aspectRatio: 1, borderRadius: 14 }} />
          <div className="skeleton" style={{ aspectRatio: 1, borderRadius: 14 }} />
        </Grid>
      ) : items.length === 0 ? (
        <EmptyState>
          <div style={{ fontSize: 48, marginBottom: 12 }}><FiImage /></div>
          <div style={{ fontSize: 18, fontWeight: 600, marginBottom: 8 }}>Aucune photo</div>
          <div>Uploadez vos premières photos ci-dessus</div>
        </EmptyState>
      ) : (
        <Grid>
          {items.map(p => (
            <PhotoCard key={p._id}>
              <Img src={getPhotoUrl(p.url)} alt="" />
              {p.caption && <PhotoCaption>{p.caption}</PhotoCaption>}
            </PhotoCard>
          ))}
        </Grid>
      )}
    </Container>
  );
}
