import React, { useState } from 'react';
import styled from 'styled-components';
import { authService } from '../services/authService';

const Wrap = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const Card = styled.div`
  width: 100%;
  max-width: 440px;
  background: var(--card-bg);
  border: 1px solid var(--border-color);
  border-radius: 24px;
  padding: 40px 32px;
  box-shadow: var(--shadow-hover);
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: "";
    position: absolute;
    inset: 0;
    border-radius: 24px;
    padding: 2px;
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    opacity: 0.4;
  }
  
  @media (max-width: 480px) {
    padding: 32px 24px;
  }
`;

const Logo = styled.div`
  text-align: center;
  margin-bottom: 32px;
`;

const LogoText = styled.h1`
  margin: 0;
  font-size: 32px;
  font-weight: 800;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.5px;
`;

const Title = styled.h2`
  margin: 0 0 8px 0;
  font-size: 24px;
  font-weight: 700;
  color: var(--text-color);
  text-align: center;
`;

const Subtitle = styled.p`
  margin: 0 0 32px 0;
  font-size: 14px;
  color: var(--muted-text);
  text-align: center;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Label = styled.label`
  font-size: 13px;
  font-weight: 600;
  color: var(--text-color);
  letter-spacing: 0.3px;
`;

const Input = styled.input`
  width: 100%;
  padding: 14px 16px;
  background: rgba(255,255,255,0.04);
  border: 2px solid var(--border-color);
  border-radius: 12px;
  color: var(--text-color);
  font-size: 15px;
  transition: all .25s ease;
  outline: none;
  
  &:focus {
    border-color: var(--neon-1);
    background: rgba(255,255,255,0.06);
    box-shadow: 0 0 0 4px rgba(139,92,246,0.1);
  }
  
  &::placeholder {
    color: var(--muted-text);
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 16px;
  background: var(--gradient-primary);
  color: #fff;
  border: none;
  border-radius: 14px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all .25s cubic-bezier(.21,.65,.22,.99);
  box-shadow: 0 4px 16px rgba(139,92,246,0.3);
  position: relative;
  overflow: hidden;
  margin-top: 8px;
  
  &::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, rgba(255,255,255,0.2), transparent);
    opacity: 0;
    transition: opacity .25s ease;
  }
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 32px rgba(139,92,246,0.45);
  }
  
  &:hover::before {
    opacity: 1;
  }
  
  &:active {
    transform: translateY(0);
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none !important;
  }
`;

const SwitchContainer = styled.div`
  text-align: center;
  margin-top: 24px;
  padding-top: 24px;
  border-top: 1px solid var(--border-color);
`;

const SwitchButton = styled.button`
  background: none;
  border: none;
  color: var(--neon-1);
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all .2s ease;
  
  &:hover {
    color: var(--neon-3);
    text-decoration: underline;
  }
`;

export default function Login({ onLogin }) {
  const [mode, setMode] = useState('login');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [busy, setBusy] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setBusy(true);
    try {
      if (mode === 'login') {
        await authService.login(email, password);
      } else {
        await authService.register(name, email, password);
      }
      onLogin && onLogin();
    } catch (e) {
      alert((e?.response?.data?.error) || e.message);
    }
    setBusy(false);
  };

  return (
    <Wrap>
      <Card className="fade-in">
        <Logo>
          <LogoText>US</LogoText>
        </Logo>
        <Title>{mode === 'login' ? 'Bienvenue' : 'Créer un compte'}</Title>
        <Subtitle>
          {mode === 'login' 
            ? 'Connectez-vous pour continuer' 
            : 'Créez votre compte pour commencer'}
        </Subtitle>
        <Form onSubmit={submit}>
          {mode === 'register' && (
            <InputGroup>
              <Label>Nom</Label>
              <Input
                placeholder="Votre nom"
                value={name}
                onChange={e => setName(e.target.value)}
                required
              />
            </InputGroup>
          )}
          <InputGroup>
            <Label>Email</Label>
            <Input
              type="email"
              placeholder="vous@exemple.com"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
          </InputGroup>
          <InputGroup>
            <Label>Mot de passe</Label>
            <Input
              type="password"
              placeholder="••••••••"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
          </InputGroup>
          <Button type="submit" disabled={busy}>
            {busy ? 'Chargement...' : (mode === 'login' ? 'Se connecter' : "S'inscrire")}
          </Button>
        </Form>
        <SwitchContainer>
          <SwitchButton onClick={() => setMode(mode === 'login' ? 'register' : 'login')}>
            {mode === 'login' ? "Pas encore de compte ? S'inscrire" : 'Déjà inscrit ? Se connecter'}
          </SwitchButton>
        </SwitchContainer>
      </Card>
    </Wrap>
  );
}
